// src/hooks/useLLM.js
import { useState } from 'react';
import { generateRecipeResponse } from '../services/api';

export const useLLM = () => {
  const [isLoading, setIsLoading] = useState(false);

  const generateResponse = async (prompt, context) => {
    setIsLoading(true);
    try {
      // This would connect to your actual LLM API
      const response = await generateRecipeResponse(prompt, context);
      return response;
    } catch (error) {
      console.error('LLM Error:', error);
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  return { generateResponse, isLoading };
};
