// src/components/LLMIntegration.js
import React, { useState, useEffect } from 'react';
import { Send, Bot, User, AlertCircle, Key } from 'lucide-react';
import { useLLM } from '../hooks/useLLM';

const LLMIntegration = ({ recipes, files }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const { generateResponse, isLoading, error } = useLLM();
  const [apiStatus, setApiStatus] = useState('checking');

  // Check API status on component mount
  useEffect(() => {
    checkAPIStatus();
  }, []);

  const checkAPIStatus = async () => {
    // Simple check to see if we have API keys configured
    const hasOpenAIKey = !!process.env.REACT_APP_OPENAI_API_KEY;
    const hasHuggingFaceKey = !!process.env.REACT_APP_HUGGINGFACE_API_KEY;
    
    if (hasOpenAIKey || hasHuggingFaceKey) {
      setApiStatus('configured');
    } else {
      setApiStatus('not-configured');
      
      // Add welcome message with setup instructions
      setMessages([{
        role: 'assistant',
        content: `Welcome to Recipe AI Assistant! 🍳\n\nTo enable AI responses, please add your API key:\n\n1. Get an API key from OpenAI or HuggingFace\n2. Create a .env file with REACT_APP_OPENAI_API_KEY=your_key\n3. Restart the application\n\nFor now, I'll provide demo responses. How can I help with your recipes?`
      }]);
    }
  };

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      const context = {
        recipes: recipes,
        files: files
      };

      const response = await generateResponse(input, context);
      const botMessage = { role: 'assistant', content: response };
      setMessages(prev => [...prev, botMessage]);
      
    } catch (error) {
      const errorMessage = { 
        role: 'assistant', 
        content: `I apologize, but I'm having trouble connecting to the AI service. Please check your API configuration or try again later.\n\nError: ${error.message}`
      };
      setMessages(prev => [...prev, errorMessage]);
    }
  };

  const handleQuickQuestion = (question) => {
    setInput(question);
    setTimeout(() => handleSend(), 100);
  };

  return (
    <div className="llm-integration">
      <h2>AI Recipe Assistant</h2>
      
      {apiStatus === 'not-configured' && (
        <div className="api-warning">
          <AlertCircle size={20} />
          <span>API not configured - using demo mode</span>
          <button onClick={() => window.open('https://platform.openai.com/api-keys', '_blank')}>
            <Key size={16} />
            Get API Key
          </button>
        </div>
      )}
      
      {error && (
        <div className="error-message">
          <AlertCircle size={20} />
          <span>{error}</span>
        </div>
      )}

      <div className="quick-questions">
        <h4>Quick Questions:</h4>
        <div className="quick-buttons">
          <button onClick={() => handleQuickQuestion('How can I make my recipes healthier?')}>
            Make healthier
          </button>
          <button onClick={() => handleQuickQuestion('Give me quick recipe ideas')}>
            Quick recipes
          </button>
          <button onClick={() => handleQuickQuestion('Vegetarian substitutions?')}>
            Vegetarian options
          </button>
          <button onClick={() => handleQuickQuestion('Ingredient substitutions')}>
            Substitutions
          </button>
        </div>
      </div>
      
      <div className="chat-container">
        <div className="messages">
          {messages.length === 0 ? (
            <div className="welcome-message">
              <Bot size={48} />
              <h3>Welcome to Recipe AI Assistant!</h3>
              <p>I can help you with recipe suggestions, modifications, cooking techniques, and more based on your recipes and uploaded files.</p>
              <p>Ask me anything about cooking! 🍳</p>
            </div>
          ) : (
            messages.map((message, index) => (
              <div key={index} className={`message ${message.role}`}>
                <div className="message-avatar">
                  {message.role === 'user' ? <User size={20} /> : <Bot size={20} />}
                </div>
                <div className="message-content">
                  <p>{message.content}</p>
                </div>
              </div>
            ))
          )}
          {isLoading && (
            <div className="message assistant">
              <div className="message-avatar">
                <Bot size={20} />
              </div>
              <div className="message-content">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="input-area">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Ask about recipes, cooking techniques, or get suggestions..."
            disabled={isLoading}
          />
          <button onClick={handleSend} disabled={isLoading || !input.trim()}>
            <Send size={20} />
          </button>
        </div>
      </div>

      <div className="context-info">
        <h4>Available Context:</h4>
        <p>📋 Recipes: {recipes.length} | 📁 Files: {files.length}</p>
      </div>
    </div>
  );
};

export default LLMIntegration;
