// src/components/LLMIntegration.js
import React, { useState } from 'react';
import { Send, Bot, User } from 'lucide-react';
import { useLLM } from '../hooks/useLLM';

const LLMIntegration = ({ recipes, files }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const { generateResponse, isLoading } = useLLM();

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      const context = {
        recipes: recipes,
        files: files.map(f => f.name)
      };

      const response = await generateResponse(input, context);
      const botMessage = { role: 'assistant', content: response };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      const errorMessage = { 
        role: 'assistant', 
        content: 'Sorry, I encountered an error. Please try again.' 
      };
      setMessages(prev => [...prev, errorMessage]);
    }
  };

  return (
    <div className="llm-integration">
      <h2>AI Recipe Assistant</h2>
      
      <div className="chat-container">
        <div className="messages">
          {messages.length === 0 ? (
            <div className="welcome-message">
              <Bot size={48} />
              <h3>Welcome to Recipe AI Assistant!</h3>
              <p>I can help you with recipe suggestions, modifications, and more based on your uploaded recipes and files.</p>
            </div>
          ) : (
            messages.map((message, index) => (
              <div key={index} className={`message ${message.role}`}>
                <div className="message-avatar">
                  {message.role === 'user' ? <User size={20} /> : <Bot size={20} />}
                </div>
                <div className="message-content">
                  <p>{message.content}</p>
                </div>
              </div>
            ))
          )}
          {isLoading && (
            <div className="message assistant">
              <div className="message-avatar">
                <Bot size={20} />
              </div>
              <div className="message-content">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="input-area">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Ask about recipes, suggest modifications, or get cooking tips..."
            disabled={isLoading}
          />
          <button onClick={handleSend} disabled={isLoading}>
            <Send size={20} />
          </button>
        </div>
      </div>

      <div className="context-info">
        <h4>Available Context:</h4>
        <p>Recipes: {recipes.length} | Files: {files.length}</p>
      </div>
    </div>
  );
};

export default LLMIntegration;
